import React, { Fragment, useState, useRef } from "react";
import * as seeMessage from "./Message.module.css";
import ShowMessage from "./ShowMessage";

const Message = (props) => {
  const [isShowMessage, setShowMessage] = useState(false);
  const [error, setError] = useState("");
  const code = useRef("");
  const validationForm = (e) => {
    e.preventDefault();
    const data = code.current.value;

    if ( data.trim() === "") {
      setError("Secret Code Field is Empty !");
      setModel(() => true);
    } else if (data.message.trim() === "" && data.code.trim() === "") {
      setError("Both Fields are Empty !");
      setModel(() => true);
    } else if (
      data.message.trim() !== "" &&
      data.code.trim() !== "" &&
      data.code.length < 5
    ) {
      setError("Code must contain at least 5 characters!");
      setModel(() => true);
    }
  };
  const hideModelHanler = () => {
    setModel(() => false);
  };
  const messageHandler = (e) => {
    e.preventDefault();
    setShowMessage(() => true);
  };
  return (
    <Fragment>
      {!isShowMessage && (
        <div className={seeMessage["form-container"]}>
          <form>
            <label for="secret-code">Enter Secret Code:</label>
            <input type="password" />
            <br />
            <button
              className={seeMessage.btn}
              type="submit"
              class="btn"
              onClick={messageHandler}
            >
              submit
            </button>
          </form>
        </div>
      )}
      {isShowMessage && <ShowMessage />}
    </Fragment>
  );
};

export default Message;
