import React, { Fragment, useState } from "react";
import Model from "../UI/Model";

import * as addMessageStyle from "./AddMessage.module.css";

const AddMessage = (props) => {
  const [isModel, setModel] = useState(false);
  const validationForm = (e) => {
    e.preventDefault()
    setModel(() => true);
  };
  const hideModelHanler = () => {
    setModel(() => false);
  };
  return (
    <Fragment>
      {isModel && <Model hide={hideModelHanler} />}
      <div className={addMessageStyle["form-container"]}>
        <form>
          <label for="message">Message:</label>
          <textarea id="message" name="message" rows="10" cols="30" />
          <br />
          <label for="secret-code">Set Secret Code:</label>
          <input type="password" />
          <br />
          <button
            className={addMessageStyle.btn}
            type="submit"
            class="btn"
            onClick={validationForm}
          >
            Add
          </button>
        </form>
      </div>
    </Fragment>
  );
};

export default AddMessage;
