import React, { useState, useEffect } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movieData, updateMoviesData] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  //Get Method by default
  const fetchingMoviesHandler = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch("https://swapi.dev/api/films");
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const data = await response.json();
      const moviesData = data.results.map((movie) => {
        return {
          id: movie.episode_id,
          openingText: movie.opening_crawl,
          title: movie.title,
          releaseDate: movie.release_date,
        };
      });
      updateMoviesData(moviesData);
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
  };
  const content=<p>Found no Movies</p>;
  if(movieData.length>0){
    content=<MoviesList movies={movieData} />
  }
  if(error){
    content=<p>{error}</p>
  }
  if(isLoading){
    content= <p>Loading....</p>
  }


  return (
    <React.Fragment>
      <section>
        <button onClick={fetchingMoviesHandler}>Fetch Movies</button>
      </section>

      <section>
        {content }
      </section>
    </React.Fragment>
  );
}

export default App;
