import React, { Fragment, useCallback, useRef, useState } from "react";
import Model from "../UI/Model";

import * as addMessageStyle from "./AddMessage.module.css";

const AddMessage = (props) => {
  const [isModel, setModel] = useState(false);
  const [error, setError] = useState("");
  const [secretMsgData, setSecretMsgData] = useState({
    message: "",
    code: "",
  });
  const message = useRef("");
  const code = useRef("");
  const savingDataHandler = useCallback(async () => {
    console.log(secretMsgData);
    const response = await fetch("http://localhost:9000/messages", {
      method: "POST",
      body: JSON.stringify(secretMsgData),
      headers: {
        "content-type": "application/json",
      },
    });
    const data = await response.json();
    console.log(data);
  }, []);
  const validationForm = (e) => {
    e.preventDefault();
    const data = {
      message: message.current.value,
      code: code.current.value,
    };
    if (data.message.trim() === "" && data.code.trim() !== "") {
      setError("Message Filed is Empty");
      setModel(() => true);
    } else if (data.message.trim() !== "" && data.code.trim() === "") {
      setError("Secret Code Field is Empty !");
      setModel(() => true);
    } else if (data.message.trim() === "" && data.code.trim() === "") {
      setError("Both Fields are Empty !");
      setModel(() => true);
    } else if (
      data.message.trim() !== "" &&
      data.code.trim() !== "" &&
      data.code.length < 5
    ) {
      setError("Code must contain at least 5 characters!");
      setModel(() => true);
    } else {
      setSecretMsgData({
        ...secretMsgData,
        message: data.message,
        code: data.code,
      });
      savingDataHandler();
    }
  };
  const hideModelHanler = () => {
    setModel(() => false);
  };

  return (
    <Fragment>
      {isModel && <Model hide={hideModelHanler} errorMsg={error} />}
      <div className={addMessageStyle["form-container"]}>
        <form>
          <label for="message">Message:</label>
          <textarea
            id="message"
            name="message"
            rows="10"
            cols="30"
            ref={message}
          />
          <br />
          <label for="secret-code">Set Secret Code:</label>
          <input type="password" ref={code} />
          <br />
          <button
            className={addMessageStyle.btn}
            type="submit"
            class="btn"
            onClick={validationForm}
          >
            Add
          </button>
        </form>
      </div>
    </Fragment>
  );
};

export default AddMessage;
