import React, { useState, useEffect, useCallback } from "react";
import AddMovie from "./components/AddMovie";
import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movieData, updateMoviesData] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  //Get Method by default
  const fetchingMoviesHandler = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(
        "https://react-muhib-default-rtdb.firebaseio.com/movies.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const data = await response.json();
      const loadedMovies=[];
      for(const key of loadedMovies){
        loadedMovies.push({

        })
      }
      const moviesData = data.results.map((movie) => {
        return {
          id: movie.episode_id,
          openingText: movie.opening_crawl,
          title: movie.title,
          releaseDate: movie.release_date,
        };
      });
      updateMoviesData(moviesData);
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
  }, []);

  useEffect(() => {
    fetchingMoviesHandler();
  }, [fetchingMoviesHandler]);

  async function addMovieHandler(movie) {
    const response = await fetch(
      "https://react-muhib-default-rtdb.firebaseio.com/movies.json",
      {
        method: "POST",
        body: JSON.stringify(movie),
        headers: {
          "content-type": "application/json",
        },
      }
    );
    const data = response.json();
    console.log(data);
  }

  let content = <p>Found no Movies</p>;
  if (movieData.length > 0) {
    content = <MoviesList movies={movieData} />;
  }
  if (error) {
    content = <p>{error}</p>;
  }
  if (isLoading) {
    content = <p>Loading....</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={fetchingMoviesHandler}>Fetch Movies</button>
      </section>

      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
