import React, { Fragment, useReducer, useRef, useState } from "react";
import Model from "../UI/Model";

import * as addMessageStyle from "./AddMessage.module.css";

const AddMessage = (props) => {
  const defaultValue = {
    error: "",
    isModel: false,
  };
  const message = useRef("");
  const code = useRef("");
  const reducer = (state, action) => {
    if (action.type === "validationForm") {
      const data = {
        message: message.current.value,
        code: code.current.value,
      };
      if (data.message.trim() === "" && data.code.trim() !== "") {
        return {
          error: "Message Filed is Empty",
          isModel: true,
        };
      } else if (data.message.trim() !== "" && data.code.trim() === "") {
        return {
          error: "Secret Code Field is Empty !",
          isModel: true,
        };
      } else if (data.message.trim() === "" && data.code.trim() === "") {
        return {
          error: "Both Fields are Empty !",
          isModel: true,
        };
      }
    }
    if (action.type === "hideModelHanler") {
      return {
        ...state,
        isModel: false,
      };
    }
  };
  const [state, dispatchReducer] = useReducer(reducer, defaultValue);
  const validationForm = (e) => {
    e.preventDefault();
    dispatchReducer({
      type: "validationForm ",
    });
  };
  const hideModelHanler = () => {
    dispatchReducer({
      type: "hideModelHanler ",
    });
  };
  return (
    <Fragment>
      {state.isModel && <Model hide={hideModelHanler} errorMsg={state.error} />}
      <div className={addMessageStyle["form-container"]}>
        <form>
          <label htmlFor="message">Message:</label>
          <textarea
            id="message"
            name="message"
            rows="10"
            cols="30"
            ref={message}
          />
          <br />
          <label htmlFor="secret-code">Set Secret Code:</label>
          <input type="password" ref={code} />
          <br />
          <button
            className={addMessageStyle.btn}
            type="submit"
            class="btn"
            onClick={validationForm}
          >
            Add
          </button>
        </form>
      </div>
    </Fragment>
  );
};

export default AddMessage;
