import React, { useState, useEffect } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movieData, updateMoviesData] = useState([]);
  const [isLoading, setLoading] = useState(false);
  //Get Method by default
  const fetchingMoviesHandler = async () => {
    setLoading(true);
    const response = await fetch("https://swapi.dev/api/films/");
    const data = await response.json();
    const moviesData = data.results.map((movie) => {
      return {
        id: movie.episode_id,
        openingText: movie.opening_crawl,
        title: movie.title,
        releaseDate: movie.release_date,
      };
    });
    setLoading(false);
    updateMoviesData(moviesData);
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchingMoviesHandler}>Fetch Movies</button>
      </section>

      <section>
      {!isLoading && <p>No movies found!</p>}
      {!isLoading && <MoviesList movies={movieData} />}</section>
      {isLoading && <p>Loading....</p>}
    </React.Fragment>
  );
}

export default App;
